// Combinar análises
const deepseekAnalysis = $node["Deepseek - Product Analysis"].json;
const geminiAnalysis = $node["Gemini - Product Optimization"].json;

// Extrair conteúdo da resposta do Deepseek
let deepseekContent = '';
if (deepseekAnalysis.choices && deepseekAnalysis.choices[0] && deepseekAnalysis.choices[0].message) {
  deepseekContent = deepseekAnalysis.choices[0].message.content;
}

// Extrair conteúdo da resposta do Gemini
let geminiContent = '';
if (geminiAnalysis.text) {
  geminiContent = geminiAnalysis.text;
}

return {
  success: true,
  produto: $node["Process Product Data"].json.produto,
  analise_marketing: deepseekContent,
  otimizacao_ecommerce: geminiContent,
  timestamp: new Date().toISOString(),
  sugestoes_combinadas: {
    titulo: "Sugestões para melhorar o título e nome do produto",
    descricao: "Sugestões para otimizar a descrição do produto",
    seo: "Palavras-chave e estratégias de SEO recomendadas",
    preco: "Estratégias de precificação sugeridas",
    categorias: "Categorias adicionais recomendadas",
    imagens: "Sugestões para melhorar as imagens do produto"
  }
};
